  `hits` mediumint(8) NOT NULL DEFAULT '0' COMMENT '真实点击',
  `vhits` mediumint(8) NOT NULL DEFAULT '0' COMMENT '虚拟点击',
  `is_yc` tinyint(1) DEFAULT '0' COMMENT '是否原创',
