DROP TABLE IF EXISTS `tc_product`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `tc_product` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `create_uid` smallint(5) NOT NULL DEFAULT '0' COMMENT '用户ID',
  `edit_uid` smallint(5) NOT NULL DEFAULT '0' COMMENT '用户ID',
  `title` varchar(120) NOT NULL DEFAULT '' COMMENT '标题',
  `post_key` varchar(500) DEFAULT '' COMMENT 'TAGS',
  `smallmemo` varchar(200) DEFAULT '' COMMENT '文章简介',
  `content` mediumtext COMMENT '文章内容',
  `classid` int(10) NOT NULL DEFAULT '0' COMMENT '栏目ID',
  `addtime` int(10) NOT NULL DEFAULT '0' COMMENT '添加时间',
  `edittime` int(10) DEFAULT '0' COMMENT '编辑时间',
  `top` tinyint(1) NOT NULL DEFAULT '0' COMMENT '置顶',
  `levels` tinyint(1) NOT NULL DEFAULT '0' COMMENT '推荐',
  `special` tinyint(4) NOT NULL DEFAULT '0' COMMENT '主页头条',
  `flashpic` tinyint(1) NOT NULL DEFAULT '0' COMMENT '幻灯片',
  `forbidcomment` tinyint(1) NOT NULL DEFAULT '0' COMMENT '禁止评论',
  `fonttype` tinyint(1) NOT NULL DEFAULT '0' COMMENT '标题加粗',
  `color` varchar(20) DEFAULT '' COMMENT '标题颜色',
  `jumpurl` varchar(200) DEFAULT '' COMMENT '跳转地址',
  `ckjump` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否跳转',
  `iframeurl` varchar(200) DEFAULT '' COMMENT '跳转框架',
  `ckiframe` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否框架',
  `photo` varchar(200) DEFAULT '' COMMENT '大图',
  `photo_s` varchar(200) DEFAULT '' COMMENT '小图',
  `imgstr` text COMMENT '图片URL',
  `isphoto` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否图片文章',
  `themepath` varchar(250) DEFAULT '' COMMENT '模板路径',
  `money` int(10) DEFAULT '0' COMMENT '阅读点数',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '验证',
  `hits` mediumint(8) NOT NULL DEFAULT '0' COMMENT '真实点击',
  `vhits` mediumint(8) NOT NULL DEFAULT '0' COMMENT '虚拟点击',
  `is_yc` tinyint(1) DEFAULT '0' COMMENT '是否原创',
  `reply_num` mediumint(8) NOT NULL DEFAULT '0' COMMENT '回复',
  `reply_last` int(10) DEFAULT '0' COMMENT '最后回复时间',
  `fav_num` int(10) DEFAULT '0' COMMENT '收藏数量',
  `praise_num` int(10) DEFAULT '0' COMMENT '点赞数量',
  `belittle_num` int(10) DEFAULT '0' COMMENT '踩数量',
  `htmlTime` int(10) DEFAULT '0' COMMENT '生成静态时间',
  `is_delete` tinyint(1) DEFAULT '0' COMMENT '是否删除',
  `maket_price` int(10) DEFAULT '0' COMMENT '市场价',
  `shop_price` int(10) DEFAULT '0' COMMENT '商城价',
  `stockNum` int(10) DEFAULT '0' COMMENT '库存数量',
  PRIMARY KEY (`id`),
  KEY `title` (`title`),
  KEY `classid` (`classid`)
